import Foundation

struct TimeEntry: Identifiable, Codable, Equatable {
    let id: UUID
    var title: String
    var duration: Double
    var category: Category
    var date: Date

    init(id: UUID = UUID(), title: String, duration: Double, category: Category, date: Date = Date()) {
        self.id = id
        self.title = title
        self.duration = duration
        self.category = category
        self.date = date
    }

    enum Category: String, Codable, CaseIterable {
        case work = "Work"
        case rest = "Rest"
        case personal = "Personal"

        var color: String {
            switch self {
            case .work: return "ttPrimary"
            case .rest: return "ttSecondary"
            case .personal: return "ttText"
            }
        }
    }
}

import Foundation

enum UserDefaultsKeys {
    static let hasCompletedOnboarding = "hasCompletedOnboarding"
    static let timeEntries = "timeEntries"
}

import Foundation

class Persistence {
    static let shared = Persistence()

    private init() {}

    func saveTimeEntries(_ entries: [TimeEntry]) {
        if let encoded = try? JSONEncoder().encode(entries) {
            UserDefaults.standard.set(encoded, forKey: UserDefaultsKeys.timeEntries)
        }
    }

    func loadTimeEntries() -> [TimeEntry] {
        guard let data = UserDefaults.standard.data(forKey: UserDefaultsKeys.timeEntries),
              let entries = try? JSONDecoder().decode([TimeEntry].self, from: data) else {
            return []
        }
        return entries
    }

    func saveOnboardingCompleted() {
        UserDefaults.standard.set(true, forKey: UserDefaultsKeys.hasCompletedOnboarding)
    }

    func hasCompletedOnboarding() -> Bool {
        UserDefaults.standard.bool(forKey: UserDefaultsKeys.hasCompletedOnboarding)
    }
}

import Foundation
import SwiftUI

@MainActor
class TimeEntriesViewModel: ObservableObject {
    @Published var timeEntries: [TimeEntry] = []
    @Published var showingSuccessNotification = false

    init() {
        timeEntries = Persistence.shared.loadTimeEntries()
    }

    func addEntry(_ entry: TimeEntry) {
        timeEntries.append(entry)
        saveEntries()
        showSuccessNotification()
    }

    func updateEntry(_ entry: TimeEntry) {
        if let index = timeEntries.firstIndex(where: { $0.id == entry.id }) {
            timeEntries[index] = entry
            saveEntries()
            showSuccessNotification()
        }
    }

    func deleteEntry(_ entry: TimeEntry) {
        timeEntries.removeAll { $0.id == entry.id }
        saveEntries()
    }

    func shareEntry(_ entry: TimeEntry) -> String {
        "I spent \(String(format: "%.1f", entry.duration)) hours on \(entry.title) (\(entry.category.rawValue)) using TimeTally!"
    }

    private func saveEntries() {
        Persistence.shared.saveTimeEntries(timeEntries)
    }

    private func showSuccessNotification() {
        showingSuccessNotification = true
        DispatchQueue.main.asyncAfter(deadline: .now() + 2) { [weak self] in
            self?.showingSuccessNotification = false
        }
    }

    // MARK: - Statistics

    func dailyTotal(for date: Date) -> Double {
        timeEntries
            .filter { Calendar.current.isDate($0.date, inSameDayAs: date) }
            .reduce(0) { $0 + $1.duration }
    }

    func weeklyTotal() -> Double {
        let calendar = Calendar.current
        let now = Date()
        let startOfWeek = calendar.date(from: calendar.dateComponents([.yearForWeekOfYear, .weekOfYear], from: now))!

        return timeEntries
            .filter { calendar.isDate($0.date, equalTo: startOfWeek, toGranularity: .weekOfYear) }
            .reduce(0) { $0 + $1.duration }
    }

    func categoryBreakdown() -> [(category: TimeEntry.Category, total: Double)] {
        Dictionary(grouping: timeEntries, by: { $0.category })
            .map { (category: $0.key, total: $0.value.reduce(0) { $0 + $1.duration }) }
            .sorted { $0.total > $1.total }
    }
}

