import SwiftUI

struct AddEntryView: View {
    @ObservedObject var viewModel: TimeEntriesViewModel
    @Environment(\.dismiss) private var dismiss

    @State private var title = ""
    @State private var duration = ""
    @State private var category = TimeEntry.Category.work

    var body: some View {
        NavigationStack {
            ZStack {
                Color(.ttBackground)
                    .ignoresSafeArea()

                Form {
                    Section {
                        TextField("Activity Title", text: $title)
                            .textContentType(.none)
                            .autocorrectionDisabled()

                        TextField("Duration (hours)", text: $duration)
                            .keyboardType(.decimalPad)

                        Picker("Category", selection: $category) {
                            ForEach(TimeEntry.Category.allCases, id: \.self) { category in
                                Text(category.rawValue)
                                    .tag(category)
                            }
                        }
                    } header: {
                        Text("New Time Entry")
                    }
                }
                .scrollContentBackground(.hidden)
            }
            .navigationTitle("Add Entry")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Save") {
                        saveEntry()
                    }
                    .disabled(title.isEmpty || duration.isEmpty)
                }
            }
        }
    }

    private func saveEntry() {
        guard let durationValue = Double(duration) else { return }

        let entry = TimeEntry(
            title: title,
            duration: durationValue,
            category: category
        )

        viewModel.addEntry(entry)
        clearFields()
    }

    private func clearFields() {
        title = ""
        duration = ""
        category = .work
    }
}

import SwiftUI

struct EntryDetailView: View {
    let entry: TimeEntry
    @ObservedObject var viewModel: TimeEntriesViewModel
    @Environment(\.dismiss) private var dismiss

    @State private var showingEditSheet = false
    @State private var showingDeleteAlert = false

    var body: some View {
        ZStack {
            Color(.ttBackground)
                .ignoresSafeArea()

            VStack(spacing: 24) {
                detailCard
                actionButtons
                Spacer()
            }
            .padding()
        }
        .navigationTitle("Entry Details")
        .navigationBarTitleDisplayMode(.inline)
        .sheet(isPresented: $showingEditSheet) {
            EditEntryView(entry: entry, viewModel: viewModel)
        }
        .alert("Delete Entry", isPresented: $showingDeleteAlert) {
            Button("Delete", role: .destructive) {
                deleteEntry()
            }
            Button("Cancel", role: .cancel) {}
        } message: {
            Text("Are you sure you want to delete this entry?")
        }
    }

    private var detailCard: some View {
        VStack(spacing: 16) {
            Text(entry.title)
                .font(.title2)
                .fontWeight(.bold)
                .foregroundColor(Color(.ttText))

            HStack(spacing: 24) {
                DetailItem(title: "Duration",
                          value: String(format: "%.1f hrs", entry.duration),
                          color: Color(.ttPrimary))

                DetailItem(title: "Category",
                          value: entry.category.rawValue,
                          color: Color(entry.category.color))

                DetailItem(title: "Date",
                          value: entry.date.formatted(date: .abbreviated, time: .omitted),
                          color: Color(.ttSecondary))
            }
        }
        .padding()
        .background(Color(.ttBackground))
        .cornerRadius(16)
        .shadow(radius: 8)
    }

    private var actionButtons: some View {
        HStack(spacing: 16) {
            ActionButton(title: "Edit", icon: "pencil", color: Color(.ttPrimary)) {
                showingEditSheet = true
            }

            ActionButton(title: "Delete", icon: "trash", color: .red) {
                showingDeleteAlert = true
            }

            ActionButton(title: "Share", icon: "square.and.arrow.up", color: Color(.ttSecondary)) {
                shareEntry()
            }
        }
    }

    private func deleteEntry() {
        viewModel.deleteEntry(entry)
        dismiss()
    }

    private func shareEntry() {
        let text = viewModel.shareEntry(entry)
        let activityVC = UIActivityViewController(activityItems: [text], applicationActivities: nil)

        if let windowScene = UIApplication.shared.connectedScenes.first as? UIWindowScene,
           let window = windowScene.windows.first,
           let rootVC = window.rootViewController {
            rootVC.present(activityVC, animated: true)
        }
    }
}

